    private void calcActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_calcActionPerformed
        // TODO add your handling code here:
        double a,b,c,d,ra1,ra2,ra3,ra4,cua1,cua2,cua3,cua4,cubo1,cubo2,cubo3,cubo4;
        a=Double.parseDouble(n1.getText());
        b=Double.parseDouble(n2.getText());
        c=Double.parseDouble(n3.getText());
        d=Double.parseDouble(n4.getText());
        ra1=op.raiz(a);
        ra2=op.raiz(b);
        ra3=op.raiz(c);
        ra4=op.raiz(d);
        r1.setText(String.valueOf(ra1));
        r2.setText(String.valueOf(ra2));
        r3.setText(String.valueOf(ra3));
        r4.setText(String.valueOf(ra4));
        cua1=op.cuadrado(a);
        cua2=op.cuadrado(b);
        cua3=op.cuadrado(c);
        cua4=op.cuadrado(d);
        cu1.setText(String.valueOf(cua1));
        cu2.setText(String.valueOf(cua2));
        cu3.setText(String.valueOf(cua3));
        cu4.setText(String.valueOf(cua4));
        cubo1=op.cubo(a);
        cubo2=op.cubo(b);
        cubo3=op.cubo(c);
        cubo4=op.cubo(d);
        cub1.setText(String.valueOf(cubo1));
        cub2.setText(String.valueOf(cubo2));
        cub3.setText(String.valueOf(cubo3));
        cub4.setText(String.valueOf(cubo4));   
    }//GEN-LAST:event_calcActionPerformed

