    private void calcActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_calcActionPerformed
        // TODO add your handling code here:
        double a,b,c,s1,s2;
        a=Double.parseDouble(texa.getText());
        b=Double.parseDouble(texb.getText());
        c=Double.parseDouble(texc.getText());
        s1=ecua.sola(a,b,c);
        s2=ecua.solb(a,b,c);
        sol1.setText(String.valueOf(s1));
        sol2.setText(String.valueOf(s2));
    }//GEN-LAST:event_calcActionPerformed

