    private void calcActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_calcActionPerformed
        // TODO add your handling code here:
        double a,b;
        String n;
        boolean t;
        a=Double.parseDouble(texb.getText());
        b=Double.parseDouble(texc.getText());
        n=(texa.getText());
        sm.hide();
        sal.hide();
        nom.setText(String.valueOf(n));
        t=comp.check(a,b);
        if (t==true){
            sal.setText(String.valueOf(a*b));
            sm.show();
            sal.show();
        }
    }//GEN-LAST:event_calcActionPerformed

