    private void calcActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_calcActionPerformed
        // TODO add your handling code here:
        double a,b,c;
        String d,e;
        d=(texa.getText());
        e=(texb.getText());
        a=Double.parseDouble(texc.getText());
        b=Double.parseDouble(texd.getText());
        c=Patrimonio.precio(a,b);
        num.setText(String.valueOf(d));
        nom.setText(String.valueOf(e));
        cost.setText(String.valueOf(c));
    }//GEN-LAST:event_calcActionPerformed

